    public class StaticResourceConverter : IValueConverter
    {
        private Dictionary<string, string> _IconMapping;

        public StaticResourceConverter()
        {
            _IconMapping = new Dictionary<string, string>();
            this.BuildIconList();
        }

        public object Convert(object value, Type targetType, object parameter, CultureInfo culture)
        {
            if (value == DependencyProperty.UnsetValue || value == null)
            {
                return DependencyProperty.UnsetValue;
            }

            return _IconMapping[value.ToString()];

            //return Application.Current.MainWindow.Resources[value];
        }

        public object ConvertBack(object value, Type targetType, object parameter, CultureInfo culture)
        {
            throw new NotSupportedException();
        }

